mapper.select:
SELECT COUNT(*) FROM (SELECT * FROM T_COMBO
AND CO_NAME LIKE CONCAT('%',CONCAT(#{name},'%'))
)

mapper.update:
UPDATE T_COMBO
SET CO_STATE = #{state}
WHERE
CO_ID = #{id}

mapper.insert:
INSERT INTO T_COMBO_ITEMS_LINK(CIL_ID,CIL_CO_ID,CIL_ITS_ID) SELECT SEQ_COMBO_ITEMS.NEXTVAL,#{comboId},M.* FROM(
(SELECT #{itemsId} FROM DUAL)
)M
