mapper.select:
SELECT COUNT(*) FROM T_ITEMS_RECORD I LEFT JOIN T_MEC_RECORD M ON M.MR_ID = I.IR_MR_ID LEFT JOIN T_CARD C ON C.CA_ID = M.MR_CA_ID LEFT JOIN T_DEPARTMENT DE ON DE.DEP_ID = I.IR_DEP_ID LEFT JOIN T_DICT D ON D.DI_CODE = I.IR_STATE WHERE D.DI_TYPE='ITEMSSTATE'
AND C.CA_NUMBER=#{cardNum} AND DE.DEP_ID = #{admindepId}
AND I.IR_STATE = #{itemsState}
AND I.IR_ITS_NAME LIKE CONCAT(CONCAT('%',#{itemsName}),'%' )

SELECT I.IR_DEP_ID,I.IR_ID,I.IR_RES,I.IR_ITS_NAME,DE.DEP_NAME,D.DI_CODE,D.DI_NAME,I.IR_ITS_PRICE FROM T_ITEMS_RECORD I LEFT JOIN T_MEC_RECORD M ON M.MR_ID = I.IR_MR_ID LEFT JOIN T_CARD C ON C.CA_ID = M.MR_CA_ID LEFT JOIN T_DEPARTMENT DE ON DE.DEP_ID = I.IR_DEP_ID LEFT JOIN T_DICT D ON D.DI_CODE = I.IR_STATE WHERE D.DI_TYPE='ITEMSSTATE'
AND C.CA_NUMBER=#{cardNum}
AND DE.DEP_ID = #{admindepId}
AND I.IR_STATE = #{itemsState}
AND I.IR_ITS_NAME LIKE CONCAT(CONCAT('%',#{itemsName}),'%' )
ORDER BY I.IR_ID DESC

mapper.update:
UPDATE 
   T_ITEMS_RECORD
SET 
   IR_PE_TIME = SYSDATE,IR_UPDATE_TIME = SYSDATE,IR_STATE = 5 ,IR_DOC_ID = #{docId} 
WHERE 
   IR_ID = #{irId}

UPDATE 
   T_ITEMS_RECORD
SET 
   IR_UPDATE_TIME = SYSDATE,IR_STATE = 3 ,IR_RES = #{result} 
WHERE 
   IR_ID = #{irId}
