mapper.select:
SELECT US.US_NAME,US.US_PHONE,MR.MR_NUMBER,IR.IR_ITS_NAME,AD.AD_NAME, TO_CHAR(IR.IR_PE_TIME,'YYYY-MM-DD HH24:MI:SS') AS PETIME FROM T_ITEMS_RECORD IR LEFT JOIN T_MEC_RECORD MR ON MR.MR_ID = IR.IR_MR_ID LEFT JOIN T_CARD CA ON CA.CA_ID = MR.MR_CA_ID LEFT JOIN T_USER US ON US.US_ID = CA.CA_USER_ID LEFT JOIN T_ADMIN AD ON AD.AD_ID = MR.MR_DOC_ID
AND US.US_NAME LIKE CONCAT('%',CONCAT(#{userName},'%'))
AND US.US_PHONE = #{userTel}
AND MR.MR_NUMBER = #{mecCode}
AND IR.IR_PE_TIME >= TO_DATE(CONCAT(#{sTime},' 23:59:59'),'YYYY-MM-DD HH24:MI:SS')
AND IR.IR_PE_TIME <= TO_DATE(CONCAT(#{eTime},' 00:00:00'),'YYYY-MM-DD HH24:MI:SS')

SELECT COUNT(*) FROM T_ITEMS_RECORD IR LEFT JOIN T_MEC_RECORD MR ON MR.MR_ID = IR.IR_MR_ID LEFT JOIN T_CARD CA ON CA.CA_ID = MR.MR_CA_ID LEFT JOIN T_USER US ON US.US_ID = CA.CA_USER_ID
AND US.US_NAME LIKE CONCAT('%',CONCAT(#{userName},'%'))
AND US.US_PHONE = #{userTel}
AND MR.MR_NUMBER = #{mecCode}
AND IR.IR_PE_TIME >= TO_DATE(CONCAT(#{sTime},' 23:59:59'),'YYYY-MM-DD HH24:MI:SS')
AND IR.IR_PE_TIME <= TO_DATE(CONCAT(#{eTime},' 00:00:00'),'YYYY-MM-DD HH24:MI:SS')
